package configuracioÃÅn;import java.awt.Component;import java.awt.Graphics;import java.awt.Insets;import java.awt.image.BufferedImage;import javax.swing.border.Border;/** * Clase para dibujar fondos ajustados. * @author Jordan Aranda Tejada */public class BgBorder implements Border{	private BufferedImage mImagen = null;	/**	 * Constructor para dibujar fondos ajustados.	 * @param pImagen ImageIO.read(new File(pathname)).	 */	public BgBorder(BufferedImage pImagen)	{		this.mImagen = pImagen;	}	@Override	public void paintBorder(Component c, Graphics g, int x, int y, int width, int height)	{		if (this.mImagen != null) {			g.drawImage(this.mImagen, 0, 0, width, height, null);		}	}	@Override	public Insets getBorderInsets(Component c)	{		return new Insets(0, 0, 0, 0);	}	@Override	public boolean isBorderOpaque()	{		return true;	}}